package ClassTestday02;

public class Test21 {

	/**
	 * 通过args数组，从控制台获取数据
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=Integer.parseInt(args[0]);	
int b=Integer.parseInt(args[1]);
System.out.print("a+b="+(a+b));
System.out.print("\ta-b="+(a-b));
System.out.print("\ta*b="+a*b);
System.out.print("\ta/b="+a/b);
System.out.print("\ta%b="+a%b);
	}

}
