package com.eoe.oop.day01;

public class Point {

	/**
	 * Point类代表屏幕上的一个点,有两个属性代表横纵坐标xy
	 * @param args
	 */
	int x;
	int y;
	//无参数		
	public Point() {
		super();
	}

    //有2个参数
	public Point(int x, int y) {
		super();
		this.x = x;
		this.y = y;
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String c="d d";
		c.toString();
		System.out.println(c);

	}

}
