package com.eoe.basicLearn.day02;

public class Test11 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=100;
		System.out.println("i++:"+i++);
		i=100;
		System.out.println("++i:"+(++i));
		System.out.println("i+=5:"+(i+=5));
		System.out.println("i--:"+i--);
		System.out.println("--i:"+--i);
		System.out.println("i-5="+(i-=5));
	}

}
